changeSet.sql:
CREATE TABLE event_days
(
    id VARCHAR(36) PRIMARY KEY NOT NULL,
    name VARCHAR(255) NOT NULL,
    day_of_event INT NOT NULL
);

CREATE TABLE helpers
(
    base_attendee_id VARCHAR(36) PRIMARY KEY NOT NULL,
    FOREIGN KEY (base_attendee_id) REFERENCES base_attendees (id)
);

CREATE TABLE helper_days
(
    event_days_id VARCHAR(36) NOT NULL,
    helpers_id     VARCHAR(36) NOT NULL,
    FOREIGN KEY (event_days_id) REFERENCES event_days (id),
    FOREIGN KEY (helpers_id) REFERENCES helpers (base_attendee_id)
);
